
public class Chars {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * char is a primitive type that holds a single character
		 * 
		 * A character is written in a single quote:
		 * 
		 * 		char letter = 'w';
		 * 
		 * 		'abc' <-- makes no sense
		 */
		
		String teacher = "Hitron";
		char x = teacher.charAt(2);
		String y = teacher.substring(2, 3);
		System.out.println(x);
		System.out.println(y);
		/*
		 * static Character methods
		 * 
		 * 		char toUpperCase(char c)
		 * 		char toLowerCase(char c)
		 * 		boolean isDigit(char c)
		 */
	}
	
	

}
